		include		"Debugger.asm"		include		"SRC/SYSTEM/RAM.I"		include		"SRC/SYSTEM/MACRO.I"		include		"SRC/SYSTEM/HEADER.ASM"		INT:ERROR:		move.l	#CRAMWRITE,	(VDPCTRL)		;	Set VDP control mode to manually write a palette to CRAM		move.w	#$000E,		VDPDATA			;	Palleting...		BRA.S		INT				INCLUDE		"SRC/SYSTEM/HBLANK.ASM"		INCLUDE		"SRC/SYSTEM/VBLANK.ASM"		START:		LEA		$FF0000,	A6		MOVEQ	#0,	D7		MOVE.W	#($FE00/4)-1,	D6			@CLEARRAM:		MOVE.L	D7,	(A6)+		DBF		D6,	@CLEARRAM		BSR.W	INITVDP		BSR.W	INITJOYPADS		MOVE.B	#0*4,	(GAMEMODE).W		MAINLOOP:		MOVE.B	(GAMEMODE).W,	D0		ANDI.W	#%00011100,	D0		JSR		@INDEX(PC,D0.W)		BRA.S	MAINLOOP			@INDEX:		BRA.W	LOGO		RTS				INCLUDE		"SRC/SYSTEM/INITVDP.ASM"		INCLUDE		"SRC/SYSTEM/INITJOYPADS.ASM"		INCLUDE		"SRC/SUB/READJOYPADS.ASM"		INCLUDE		"SRC/SUB/CLEARSCREEN.ASM"		INCLUDE		"SRC/SUB/DRAWTILEMAP.ASM"		INCLUDE		"SRC/SUB/VSYNC.ASM"				INCLUDE		"SRC/GAMEMODE/LOGO.ASM"			ALIGN	$80000		include	"ErrorHandler.asm"